mapper.select:
select * 
from article
order by regDate desc;

mapper.update:
update article
set title = #{title}, content = #{content} 
where articleId = #{articleId};

update article
set liked = #{liked} + 1
where articleId = #{articleId};

update article
set isChecked = isChecked ^ 1
where articleId = #{articleId};

update article
set isPublic = isPublic ^ 1
where articleId = #{articleId};
